mapper.select:
SELECT d.id AS id, d.name AS name, d.sex AS sex, d.tel AS tel, d.school AS school, d.degree AS degree, d.job AS job, md.name AS deptName, ds.name AS subName, d.recommended AS recommended, d.status AS status FROM doctor d JOIN medical_dept_sub_and_doctor sd ON sd.doctor_id = d.id JOIN medical_dept_sub ds ON sd.dept_sub_id = ds.id JOIN medical_dept md ON ds.dept_id = md.id WHERE 1=1
AND d.name LIKE CONCAT('%', #{name}, '%')
AND md.id = #{deptId}
AND d.degree = #{degree}
AND d.job = #{job}
AND d.recommended = #{recommended}
AND d.status = #{status}
ORDER BY md.id ${order}
LIMIT #{length} OFFSET #{start}

SELECT COUNT(*)
FROM doctor d
JOIN medical_dept_sub_and_doctor sd ON sd.doctor_id = d.id
JOIN medical_dept_sub ds ON sd.dept_sub_id = ds.id
JOIN medical_dept md ON ds.dept_id = md.id
WHERE 1=1

    AND d.name LIKE CONCAT('%', #{name}, '%')


    AND md.id = #{deptId}


    AND d.degree = #{degree}


    AND d.job = #{job}


    AND d.recommended = #{recommended}

AND d.status = #{status}

mapper.update:
INSERT INTO doctor(id, photo)
VALUES(#{id}, #{photo})
    ON DUPLICATE KEY UPDATE photo = #{photo}

mapper.select:
SELECT id
FROM doctor
WHERE uuid = #{uuid}
