mapper.select:
SELECT d."id" AS "id", d."name" AS "doctorName", d."sex" AS "sex", d."tel" AS "tel", d."job" AS "job", md."name" AS "deptName", ds."name" AS "deptSubName", p."price_1" AS "price_1", p."price_2" AS "price_2" FROM DOCTOR d JOIN MEDICAL_DEPT_SUB_AND_DOCTOR sd ON sd."doctor_id" = d."id" JOIN MEDICAL_DEPT_SUB ds ON sd."dept_sub_id" = ds."id" JOIN MEDICAL_DEPT md ON ds."dept_id" = md."id" JOIN DOCTOR_PRICE p ON p."doctor_id" = d."id" WHERE 1=1
AND d."name" LIKE '%${name}%'
AND d."job" = #{job}
LIMIT ${length} OFFSET #{start}

mapper.insert:
            UPSERT INTO DOCTOR_PRICE(
    "id","doctor_id","level","price_1","price_2"
)
VALUES (
    NEXT VALUE FOR DOCTOR_PRICE_SEQUENCE, #{doctorId}, '科室主任', #{price_1},
    #{price_2}
    )
